import React,{useEffect,useState} from 'react'
import {Grid,TextField,InputLabel,MenuItem,FormControl,Select} from '@mui/material'
import {SearchWrapper,TextClasses,SideSearch,PageName} from './styled';
import Cards from '../../components/Cards'
import axios from 'axios'


const Home=()=>{
    const api_key='b88d2c99815f98160d7e7d60aba9664f';
    const FEATURED_API =`https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=${api_key}&page=1`;
    const [movies,setMovies]=useState([]);
    const [searchText,setSearchText]=useState('');

//const SEARCH_API = 'https://api.themoviedb.org/3/search/movie?&api_key= b88d2c99815f98160d7e7d60aba9664f&query=';

    useEffect(()=>{
      axios.get(FEATURED_API)
       .then(response=> {
           console.log('response',response.data.results)
           setMovies(response.data.results)
       })
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    const [select, setSelect] = useState('');
    const filterData=[
      {name:'Azalan Popülerlik',id:1},
      {name:'Artan Popülerlik',id:2},
      {name:'Artan Oylar',id:3},
      {name:'Azalan Oylar',id:4},
      {name:'Azalan Oylar',id:4},
      {name:'Azalan Oylar',id:4},
    ]

    const handleChange = (event) => {
      setSelect(event.target.value);
    };

    return(

        <Grid container rowSpacing={1} columnSpacing={{ xs: 2, sm: 2, md: 1 }}>
          <PageName >
           Popular Films
          </PageName>
        <SearchWrapper>
             <TextClasses as={TextField} InputLabelProps={{
               style: { color: '#121858' },
              }}
             id="outlined-basic" value={searchText} label="Search" variant="outlined"  onChange={event => { setSearchText(event.target.value)}}/>
          </SearchWrapper>
          <Grid item xs={4}>
            <SideSearch>
            <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Sonuç Sırlama</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={select}
          label="Sonuç Sıralama"
          onChange={handleChange}
        >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
            </SideSearch>
           </Grid>
            {movies.length && movies.map((movie)=>
              <Grid item xs={2} key={movie.id}>
              <Cards  data={movie}/>
              </Grid>
            )}
            </Grid>
    )
}

export default Home
