import React,{useEffect,useState} from 'react'
import {Grid,TextField,InputLabel,MenuItem,FormControl,Select,CircularProgress} from '@mui/material'
import {SearchWrapper,TextClasses,SideSearch,PageName} from './styled';
import Cards from '../../components/Cards'
import axios from 'axios'
import { useTranslation } from 'react-i18next';


const Home=()=>{
    const api_key='b88d2c99815f98160d7e7d60aba9664f';
    const FEATURED_API =`https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=${api_key}&page=1`;
    const SEARCH_API = `https://api.themoviedb.org/3/search/movie?&api_key=${api_key}&&query=`;
    const [movies,setMovies]=useState([]);
    const [searchText,setSearchText]=useState('');
    const [timer, setTimer] = useState(null);
    const [loading,setLoading] = useState(false)
    const {t} =useTranslation();



    useEffect(()=>{
      axios.get(FEATURED_API)
       .then(response=> {
           console.log('response',response.data.results)
           setMovies(response.data.results)
       })
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    const [select, setSelect] = useState('');
    const filterData=[
      {name:`${t('popularity_asc')}`,id:1},
      {name:`${t('popularity_desc')}`,id:2},
      {name:`${t('rating_asc')}`,id:3},
      {name:`${t('rating_desc')}`,id:4},
      {name:`${t('release_date_asc')}`,id:5},
      {name:`${t('release_date_desc')}`,id:6},
    ]

    const handleChange = (event) => {
      setSelect(event.target.value);
    };
    const handleTextChange=(event)=>{
      setSearchText(event.target.value)
      clearTimeout(timer);
      const newTimer = setTimeout(async () => {
        const keywords = event.target.value;

        if (keywords && keywords !== '') {
          setLoading(true)
          try {
            const response = await axios.get(SEARCH_API+event.target.value)
            if (response?.length > 0) {
               setLoading(false)

            } else {
              console.log('response',response)
            }
          } catch (error) {
            console.error(error);
          } finally {
            // ...
          }
        } 
      }, 1000);
      setTimer(newTimer);
    }

    return(

        <Grid container rowSpacing={1} columnSpacing={{ xs: 2, sm: 2, md: 1 }}>
        {loading ? ( <CircularProgress />)
        :  
        <div>
             <PageName >
          {t('popular_films')}
          </PageName>
        <SearchWrapper>
             <TextClasses as={TextField} InputLabelProps={{
               style: { color: '#121858' },
              }}
             id="outlined-basic" value={searchText} label={t('search')} variant="outlined"  onChange={ handleTextChange }/>
          </SearchWrapper>
          <Grid item xs={4}>
            <SideSearch>
            <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">{t('result_sorting')}</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={select}
          label={t('result_sorting')}
          onChange={handleChange}
        >
          {filterData.length && filterData.map(({id,name})=>
           <MenuItem key={id} value={id}>{name}</MenuItem>
           )}
        </Select>
      </FormControl>
            </SideSearch>
           </Grid>
            {movies.length && movies.map((movie)=>
              <Grid item xs={2} key={movie.id}>
              <Cards  data={movie}/>
              </Grid>
            )}
        </div>
      } 

            </Grid>
    )
}

export default Home
